import React from "react";
import { Row, Col } from "react-bootstrap";
import products from "../products";

export const HomeScreen = () => {
  return (
    <>
      <h2>Najnowsze Produkty</h2>
      <Row>
        {products.map((product) => (
          <Col sm={12} md={6} lg={4} xl={3} key={product._id}>
            <h3>{product.name}</h3>
          </Col>
        ))}
      </Row>
    </>
  );
};
